$(document).ready(function(){$(".promote div p, .promote div h4").slideUp();$(".promote-control .tab").click(function(){$(".promote div p, .promote div h4").stop().slideToggle()});$(".promote-control .tab").hover(function(){$(".promote div p, .promote div h4").stop().slideToggle()},function(){});$.getJSON("/db/projects.json",function(e){$(".recent").empty();$(".recent").html($(".recent").html()+"<h2>I've been busy. . . .</h2>");$.each(e.projects,function(e,t){$(".recent").html($(".recent").html()+"<div class='not-anchor'><p class='touch'>Tap Here</p><div class='project'><a href='"+t.url+"' target='_self' title='"+t.title+"'><h3>"+t.name+"</h3></a><a href='"+t.url+"' target='_self' title='"+t.title+"'><h4>Created at "+t.location+"</h4></a><div class='description'><a href='"+t.url+"' target='_self' title='"+t.title+"'><p>"+t.description+"</p></a></div><img src='"+t.img+"'  alt='"+t.alt+"' title='"+t.title+"'><div class='types'><a href='"+t.url+"' target='_self' title='"+t.title+"'><p>"+t.type+"</p></a></div></div></div>");if(screen.width<=1024){$(".not-anchor").bind("click",function(){$(".not-anchor").removeClass("clicked-anchor");$(this).addClass("clicked-anchor")})}if(e>=5){return false}})})})